// Import Firebase SDKs
import { initializeApp } from "firebase/App";
import { getFirestore } from "firebase/firestore";
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword, signOut, onAuthStateChanged } from "firebase/auth";
import { getAnalytics } from "firebase/analytics";

// Firebase configuration
const firebaseConfig = {
  apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
  authDomain: "flashcard-60334.firebaseapp.com",
  projectId: "flashcard-60334",
  storageBucket: "flashcard-60334.appspot.com", // âœ… Fixed
  messagingSenderId: "409878544664",
  appId: "1:409878544664:web:cab1d309f82a4fc62ede6d",
  measurementId: "G-P4H2HED4LM"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
const auth = getAuth(app);

// Initialize Analytics (only if window exists)
let analytics;
if (typeof window !== "undefined") {
  analytics = getAnalytics(app);
}

// ðŸ”¹ **Authentication Functions**
/**
 * Registers a new user with email & password
 */
const registerUser = async (email, password) => {
  try {
    const userCredential = await createUserWithEmailAndPassword(auth, email, password);
    return { success: true, user: userCredential.user };
  } catch (error) {
    console.error("Registration Error:", error.message);
    return { success: false, error: error.message };
  }
};

/**
 * Logs in an existing user
 */
const loginUser = async (email, password) => {
  try {
    const userCredential = await signInWithEmailAndPassword(auth, email, password);
    return { success: true, user: userCredential.user };
  } catch (error) {
    console.error("Login Error:", error.message);
    return { success: false, error: error.message };
  }
};

/**
 * Logs out the current user
 */
const logoutUser = async () => {
  try {
    await signOut(auth);
    console.log("User logged out successfully.");
  } catch (error) {
    console.error("Logout Error:", error.message);
  }
};

/**
 * Listen for authentication state changes
 */
const authStateListener = (callback) => {
  return onAuthStateChanged(auth, (user) => {
    callback(user);
  });
};

// Export Firebase instances and functions
export { app, db, auth, analytics, registerUser, loginUser, logoutUser, authStateListener };
