import { useState, useEffect } from "react";
import { authStateListener } from "../firebaseConfig";

export const useAuth = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = authStateListener(setUser);
    return () => unsubscribe();
  }, []);

  return user;
};
