import React, { useState } from "react";
import examTopics from "../data/examTopics.json"; // ‚úÖ Import exam topics

function Flashcard() {
    const [selectedExam, setSelectedExam] = useState("");
    const [selectedDomain, setSelectedDomain] = useState("");
    const [selectedConcentration, setSelectedConcentration] = useState("");
    const [flashcard, setFlashcard] = useState(null);
    const [feedback, setFeedback] = useState("");
    const [loading, setLoading] = useState(false);
    const [selectedAnswerKey, setSelectedAnswerKey] = useState(""); // ‚úÖ Track selected answer key

    // ‚úÖ Generate flashcard request
    const generateFlashcard = async () => {
        setLoading(true);
        setFeedback("");
        setFlashcard(null);
        setSelectedAnswerKey("");

        const requestTopic = `${selectedExam} - ${selectedDomain} (${selectedConcentration})`;

        try {
            const response = await fetch(
                "https://us-central1-flashcard-60334.cloudfunctions.net/generate_flashcard",
                {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({ topic: requestTopic }),
                }
            );

            if (!response.ok) {
                throw new Error("Failed to fetch flashcard.");
            }

            const data = await response.json();
            setFlashcard(data);
        } catch (error) {
            setFeedback("‚ùå Error generating flashcard. Please try again.");
            console.error("Fetch error:", error);
        } finally {
            setLoading(false);
        }
    };

    // ‚úÖ Handle changes
    const handleExamChange = (event) => {
        const exam = event.target.value;
        setSelectedExam(exam);
        setSelectedDomain("");
        setSelectedConcentration("");
    };

    const handleDomainChange = (event) => {
        const domain = event.target.value;
        setSelectedDomain(domain);
        setSelectedConcentration("");
    };

    const getDomainOptions = () => {
        if (!selectedExam) return [];
        return examTopics[selectedExam]?.domains || [];
    };

    const getConcentrationOptions = () => {
        const domain = getDomainOptions().find((d) => d.name === selectedDomain);
        return domain?.concentration_areas || [];
    };

    return (
        <div style={{ maxWidth: "600px", margin: "auto", textAlign: "center", fontFamily: "Arial, sans-serif" }}>
            <h2>üß† Flashcard Generator</h2>

            {/* ‚úÖ Select Exam */}
            <div style={{ margin: "10px" }}>
                <label htmlFor="examSelect">Select Exam:</label>
                <select
                    id="examSelect"
                    value={selectedExam}
                    onChange={handleExamChange}
                    style={{ margin: "5px", padding: "5px", width: "80%" }}
                >
                    <option value="">-- Select an Exam --</option>
                    {Object.keys(examTopics).map((exam) => (
                        <option key={exam} value={exam}>
                            {exam}
                        </option>
                    ))}
                </select>
            </div>

            {/* ‚úÖ Select Domain */}
            {selectedExam && (
                <div style={{ margin: "10px" }}>
                    <label htmlFor="domainSelect">Select Domain:</label>
                    <select
                        id="domainSelect"
                        value={selectedDomain}
                        onChange={handleDomainChange}
                        style={{ margin: "5px", padding: "5px", width: "80%" }}
                    >
                        <option value="">-- Select a Domain --</option>
                        {getDomainOptions().map((domain) => (
                            <option key={domain.name} value={domain.name}>
                                {domain.name}
                            </option>
                        ))}
                    </select>
                </div>
            )}

            {/* ‚úÖ Select Concentration */}
            {selectedDomain && (
                <div style={{ margin: "10px" }}>
                    <label htmlFor="concentrationSelect">Select Concentration Area:</label>
                    <select
                        id="concentrationSelect"
                        value={selectedConcentration}
                        onChange={(e) => setSelectedConcentration(e.target.value)}
                        style={{ margin: "5px", padding: "5px", width: "80%" }}
                    >
                        <option value="">-- Select a Concentration Area --</option>
                        {getConcentrationOptions().map((area, index) => (
                            <option key={index} value={area}>
                                {area}
                            </option>
                        ))}
                    </select>
                </div>
            )}

            {/* ‚úÖ Generate Button */}
            <button
                onClick={generateFlashcard}
                disabled={loading || !selectedConcentration}
                style={{
                    marginTop: "20px",
                    padding: "12px 20px",
                    backgroundColor: loading ? "#ccc" : "#4CAF50",
                    color: "white",
                    border: "none",
                    borderRadius: "5px",
                    cursor: loading ? "not-allowed" : "pointer",
                    fontSize: "16px"
                }}
            >
                {loading ? "‚è≥ Generating..." : "üéØ Generate Flashcard"}
            </button>

            {/* ‚úÖ Display Flashcard */}
            {flashcard && (
                <div style={{ marginTop: "20px", padding: "15px", border: "2px solid #007BFF", borderRadius: "10px", backgroundColor: "#f0f5ff" }}>
                    <h3>‚ùì {flashcard.question}</h3>
                    <ul style={{ textAlign: "left", listStyleType: "none", padding: 0 }}>
                        {Object.entries(flashcard.choices).map(([key, value]) => (
                            <li key={key} style={{ margin: "5px 0" }}>
                                <label>
                                    <input
                                        type="radio"
                                        name="answer"
                                        value={key} // ‚úÖ Track answer key (A, B, C, D)
                                        onChange={(e) => setSelectedAnswerKey(e.target.value)}
                                        style={{ marginRight: "10px" }}
                                    />
                                    {key}: {value}
                                </label>
                            </li>
                        ))}
                    </ul>

                    {/* ‚úÖ Submit Answer */}
                    <button
                        onClick={() =>
                            setFeedback(
                                selectedAnswerKey === flashcard.correct_answer
                                    ? "‚úÖ Correct!"
                                    : `‚ùå Incorrect. The correct answer is ${flashcard.correct_answer}.`
                            )
                        }
                        disabled={!selectedAnswerKey}
                        style={{
                            marginTop: "10px",
                            padding: "10px 15px",
                            backgroundColor: "#007BFF",
                            color: "white",
                            border: "none",
                            borderRadius: "5px",
                            cursor: selectedAnswerKey ? "pointer" : "not-allowed"
                        }}
                    >
                        üìù Submit Answer
                    </button>

                    <p style={{ marginTop: "15px", fontSize: "18px", color: feedback.includes("‚ùå") ? "red" : "green" }}>
                        {feedback}
                    </p>
                </div>
            )}
        </div>
    );
}

export default Flashcard;
