import React, { useState } from "react";
import examTopics from "../data/examTopics.json";

function Flashcard() {
    const [selectedExam, setSelectedExam] = useState("");
    const [selectedDomain, setSelectedDomain] = useState("");
    const [selectedConcentration, setSelectedConcentration] = useState("");
    const [flashcard, setFlashcard] = useState(null);
    const [feedback, setFeedback] = useState("");
    const [loading, setLoading] = useState(false);
    const [selectedAnswerKey, setSelectedAnswerKey] = useState("");

    const generateFlashcard = async () => {
        setLoading(true);
        setFeedback("");
        setFlashcard(null);
        setSelectedAnswerKey("");

        const requestTopic = `${selectedExam} - ${selectedDomain} (${selectedConcentration})`;

        try {
            const response = await fetch(
                "https://us-central1-flashcard-60334.cloudfunctions.net/generate_flashcard",
                {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify({ topic: requestTopic }),
                }
            );

            if (!response.ok) {
                throw new Error("Failed to fetch flashcard.");
            }

            const data = await response.json();
            setFlashcard(data);
        } catch (error) {
            setFeedback("❌ Error generating flashcard. Please try again.");
            console.error("Fetch error:", error);
        } finally {
            setLoading(false);
        }
    };

    const handleExamChange = (event) => {
        const exam = event.target.value;
        setSelectedExam(exam);
        setSelectedDomain("");
        setSelectedConcentration("");
    };

    const handleDomainChange = (event) => {
        const domain = event.target.value;
        setSelectedDomain(domain);
        setSelectedConcentration("");
    };

    const getDomainOptions = () => {
        if (!selectedExam) return [];
        return examTopics[selectedExam]?.domains || [];
    };

    const getConcentrationOptions = () => {
        const domain = getDomainOptions().find((d) => d.name === selectedDomain);
        return domain?.concentration_areas || [];
    };

    return (
        <div className="min-h-screen bg-gradient-to-b from-blue-100 to-blue-300 flex flex-col items-center font-sans text-gray-900">
            {/* Navbar */}
            <nav className="w-full bg-indigo-600 text-white py-4 shadow-md">
                <h1 className="text-center text-3xl font-bold">📚 Flashcard Generator</h1>
            </nav>

            {/* Flashcard Generator Form */}
            <div className="bg-white mt-10 p-6 rounded-xl shadow-lg w-full max-w-xl">
                <h2 className="text-xl font-semibold mb-6 text-blue-800">🔍 Generate a New Flashcard</h2>

                {/* Exam Dropdown */}
                <div className="mb-4">
                    <label htmlFor="examSelect" className="block mb-2 text-sm font-medium">Exam:</label>
                    <select
                        id="examSelect"
                        value={selectedExam}
                        onChange={handleExamChange}
                        className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-indigo-500"
                    >
                        <option value="">-- Select an Exam --</option>
                        {Object.keys(examTopics).map((exam) => (
                            <option key={exam} value={exam}>
                                {exam}
                            </option>
                        ))}
                    </select>
                </div>

                {/* Domain Dropdown */}
                {selectedExam && (
                    <div className="mb-4">
                        <label htmlFor="domainSelect" className="block mb-2 text-sm font-medium">Domain:</label>
                        <select
                            id="domainSelect"
                            value={selectedDomain}
                            onChange={handleDomainChange}
                            className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-indigo-500"
                        >
                            <option value="">-- Select a Domain --</option>
                            {getDomainOptions().map((domain) => (
                                <option key={domain.name} value={domain.name}>
                                    {domain.name}
                                </option>
                            ))}
                        </select>
                    </div>
                )}

                {/* Concentration Dropdown */}
                {selectedDomain && (
                    <div className="mb-4">
                        <label htmlFor="concentrationSelect" className="block mb-2 text-sm font-medium">Concentration Area:</label>
                        <select
                            id="concentrationSelect"
                            value={selectedConcentration}
                            onChange={(e) => setSelectedConcentration(e.target.value)}
                            className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-indigo-500"
                        >
                            <option value="">-- Select a Concentration Area --</option>
                            {getConcentrationOptions().map((area, index) => (
                                <option key={index} value={area}>
                                    {area}
                                </option>
                            ))}
                        </select>
                    </div>
                )}

                {/* Generate Button */}
                <button
                    onClick={generateFlashcard}
                    disabled={loading || !selectedConcentration}
                    className={`w-full mt-4 py-3 rounded-md text-white font-semibold transition-colors duration-300 ${
                        loading || !selectedConcentration
                            ? "bg-gray-400 cursor-not-allowed"
                            : "bg-indigo-600 hover:bg-indigo-700"
                    }`}
                >
                    {loading ? "⏳ Generating..." : "🚀 Generate Flashcard"}
                </button>
            </div>

            {/* Display Flashcard */}
            {flashcard && (
                <div className="mt-6 p-6 w-full max-w-xl bg-white rounded-xl shadow-md border border-blue-400">
                    <h3 className="text-lg font-bold text-indigo-700 mb-4">{flashcard.question}</h3>
                    <ul className="space-y-3">
                        {Object.entries(flashcard.choices).map(([key, value]) => (
                            <li key={key} className="flex items-center">
                                <input
                                    type="radio"
                                    name="answer"
                                    value={key}
                                    id={`answer-${key}`}
                                    onChange={(e) => setSelectedAnswerKey(e.target.value)}
                                    className="mr-3 accent-indigo-600"
                                />
                                <label htmlFor={`answer-${key}`} className="text-gray-700 text-md">
                                    {key}: {value}
                                </label>
                            </li>
                        ))}
                    </ul>

                    {/* Submit Answer */}
                    <button
                        onClick={() =>
                            setFeedback(
                                selectedAnswerKey === flashcard.correct_answer
                                    ? "✅ Correct!"
                                    : `❌ Incorrect! The correct answer is "${flashcard.correct_answer}".`
                            )
                        }
                        disabled={!selectedAnswerKey}
                        className="mt-4 w-full py-3 bg-green-500 hover:bg-green-600 text-white rounded-md font-semibold disabled:bg-gray-400"
                    >
                        🧠 Submit Answer
                    </button>

                    {/* Feedback Message */}
                    <p
                        className={`mt-3 text-xl ${
                            feedback.includes("✅") ? "text-green-700" : "text-red-600"
                        }`}
                    >
                        {feedback}
                    </p>
                </div>
            )}

            {/* Footer */}
            <footer className="mt-10 text-sm text-gray-500">
                © 2025 Flashcard App - Built with 💙 by Gene
            </footer>
        </div>
    );
}

export default Flashcard;
