import React, { useState } from "react";
import examTopics from "../data/examTopics.json";

function Flashcard() {
  const [selectedExam, setSelectedExam] = useState("");
  const [selectedDomain, setSelectedDomain] = useState("");
  const [selectedConcentration, setSelectedConcentration] = useState("");
  const [flashcard, setFlashcard] = useState(null);
  const [feedback, setFeedback] = useState("");
  const [loading, setLoading] = useState(false);
  const [selectedAnswerKey, setSelectedAnswerKey] = useState("");
  const [generatedFlashcards, setGeneratedFlashcards] = useState([]);

  const generateFlashcard = async () => { /* unchanged logic */ };

  const handleExamChange = (event) => { /* unchanged logic */ };
  const handleDomainChange = (event) => { /* unchanged logic */ };
  const getDomainOptions = () => { /* unchanged logic */ };
  const getConcentrationOptions = () => { /* unchanged logic */ };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-100 to-blue-300 flex flex-col items-center font-sans text-gray-900">
      <nav className="w-full bg-indigo-600 text-white py-4 shadow-md" role="navigation" aria-label="Main Navigation">
        <h1 className="text-center text-3xl font-bold">üìö Flashcard Generator</h1>
      </nav>

      <main className="bg-white mt-10 p-6 rounded-xl shadow-lg w-full max-w-xl" role="main">
        <h2 className="text-xl font-semibold mb-6 text-blue-800" aria-label="Flashcard Generator Form">üîç Generate a New Flashcard</h2>

        <div className="mb-4">
          <label htmlFor="examSelect" className="block mb-2 text-sm font-medium" aria-label="Exam Selection">Exam:</label>
          <select
            id="examSelect"
            value={selectedExam}
            onChange={handleExamChange}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-indigo-500"
            aria-label="Select an exam"
            aria-haspopup="listbox"  // Indicate it's a dropdown
            aria-expanded={!!selectedExam}  // Indicate if expanded
            aria-required="true"  // Indicate it's required
          >
            <option value="">-- Select an Exam --</option>
            {Object.keys(examTopics).map((exam) => (
              <option key={exam} value={exam} aria-label={`Exam: ${exam}`}>
                {exam}
              </option>
            ))}
          </select>
        </div>

        {selectedExam && (
          <div className="mb-4">
            <label htmlFor="domainSelect" className="block mb-2 text-sm font-medium" aria-label="Domain Selection">Domain:</label>
            <select
              id="domainSelect"
              value={selectedDomain}
              onChange={handleDomainChange}
              className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-indigo-500"
              aria-label="Select a domain"
            >
              <option value="">-- Select a Domain --</option>
              {getDomainOptions().map((domain) => (
                <option key={domain.name} value={domain.name} aria-label={`Domain: ${domain.name}`}>
                  {domain.name}
                </option>
              ))}
            </select>
          </div>
        )}

        {selectedDomain && (
          <div className="mb-4">
            <label htmlFor="concentrationSelect" className="block mb-2 text-sm font-medium" aria-label="Concentration Selection">Concentration Area:</label>
            <select
              id="concentrationSelect"
              value={selectedConcentration}
              onChange={(e) => setSelectedConcentration(e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-indigo-500"
              aria-label="Select a concentration area"
            >
              <option value="">-- Select a Concentration Area --</option>
              {getConcentrationOptions().map((area, index) => (
                <option key={index} value={area} aria-label={`Concentration Area: ${area}`}>
                  {area}
                </option>
              ))}
            </select>
          </div>
        )}

        <button
          onClick={generateFlashcard}
          disabled={loading || !selectedConcentration}
          className={`w-full mt-4 py-3 rounded-md text-white font-semibold transition-colors duration-300 ${
            loading || !selectedConcentration
              ? "bg-gray-400 cursor-not-allowed"
              : "bg-indigo-600 hover:bg-indigo-700"
          }`}
          aria-label="Generate Flashcard Button"
        >
          {loading ? "‚è≥ Generating..." : "üöÄ Generate Flashcard"}
        </button>
      </main>

      {flashcard && (
        <section className="mt-6 p-6 w-full max-w-xl bg-white rounded-xl shadow-md border border-blue-400" role="region" aria-label="Flashcard Display">
          <h3 className="text-lg font-bold text-indigo-700 mb-4" aria-label={`Flashcard Question: ${flashcard.question}`}>{flashcard.question}</h3>
          <ul className="space-y-3">
            {Object.entries(flashcard.choices).map(([key, value]) => (
              <li key={key} className="flex items-center">
                <input
                  type="radio"
                  name="answer"
                  value={key}
                  id={`answer-${key}`}
                  onChange={(e) => setSelectedAnswerKey(e.target.value)}
                  className="mr-3 accent-indigo-600"
                  aria-label={`Answer Choice ${key}: ${value}`}
                />
                <label htmlFor={`answer-${key}`} className="text-gray-700 text-md">
                  {key}: {value}
                </label>
              </li>
            ))}
          </ul>

          <button
            onClick={() =>
              setFeedback(
                selectedAnswerKey === flashcard.correct_answer
                  ? "‚úÖ Correct!"
                  : `‚ùå Incorrect! The correct answer is "${flashcard.correct_answer}".`
              )
            }
            disabled={!selectedAnswerKey}
            className="mt-4 w-full py-3 bg-green-500 hover:bg-green-600 text-white rounded-md font-semibold disabled:bg-gray-400"
            aria-label="Submit Answer Button"
          >
            üß† Submit Answer
          </button>

          <p
            className={`mt-3 text-xl ${feedback.includes("‚úÖ") ? "text-green-700" : "text-red-600"}`}
            aria-live="polite"
          >
            {feedback}
          </p>
        </section>
      )}

      <footer className="mt-10 text-sm text-gray-500" role="contentinfo">
        ¬© 2025 Flashcard App - Built with üíô by Gene
      </footer>
    </div>
  );
}

export default Flashcard;
