import { useState, useEffect } from "react";
import { loginUser, googleSignIn } from "../firebaseConfig";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../hooks/useAuth";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const user = useAuth();

  useEffect(() => {
    if (user) {
      console.log("Redirecting: User is logged in", user);
      navigate("/flashcards");
    }
  }, [user, navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    const result = await loginUser(email, password);
    if (result.success) {
      console.log("Login successful:", result.user);
      navigate("/flashcards");
    } else {
      setError(result.error);
    }
  };

  const handleGoogleSignIn = async () => {
    setError("");
    const result = await googleSignIn();
    if (result.success) {
      console.log("Google Sign-In successful:", result.user);
      navigate("/flashcards");
    } else {
      setError(result.error);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md w-96">
        <h2 className="text-2xl font-semibold mb-4">Login</h2>

        {error && <p className="text-red-500">{error}</p>}

        <form onSubmit={handleLogin} className="space-y-4">
          <input 
            type="email" 
            placeholder="Email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required 
            className="w-full p-2 border rounded"
          />
          <input 
            type="password" 
            placeholder="Password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            required 
            className="w-full p-2 border rounded"
          />
          <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">
            Login
          </button>
        </form>

        <div className="mt-4 text-center">or</div>

        {/* ðŸ”¹ Google Sign-In Button */}
        <button onClick={handleGoogleSignIn} className="w-full bg-red-500 text-white py-2 rounded mt-2 flex items-center justify-center hover:bg-red-600">
          <svg className="w-5 h-5 mr-2" viewBox="0 0 48 48">
            <path fill="#4285F4" d="M44.5 20H24v8.5h11.9C34.1 34.5 29.5 38 24 38c-7.8 0-14-6.3-14-14s6.2-14 14-14c3.6 0 6.9 1.3 9.4 3.4l6.7-6.7C35.4 3.4 30 1 24 1 11.3 1 1 11.3 1 24s10.3 23 23 23c11.4 0 22-9.3 22-22 0-1.3-.1-2.7-.5-4z"></path>
          </svg>
          Sign in with Google
        </button>
      </div>
    </div>
  );
};

export default Login;
